class WebMusics extends StatefulWidget {
  const WebMusics({Key? key}) : super(key: key);

  @override
  State<WebMusics> createState() => _WebMusicsState();
}

class _WebMusicsState extends State<WebMusics> {

  final webLinks = [
    "https://www.spotify.com/",
    "https://www.apple.com/apple-music/",
    "https://www.pandora.com/",
    "https://soundcloud.com/",
    "https://tidal.com/",
    "https://bandcamp.com/",
    "https://www.last.fm/",
    "https://music.youtube.com/",
    "https://www.deezer.com/",
    "https://play.google.com/music",
  ];

  final webLogos = [
    "data:image/png;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "",
    "data:image/png;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
  ];

  final webNames = [
    "Spotify", "Apple Music",
    "Pandora", "SoundCloud", "Tidal", "Bandcamp", "Last.fm",
    "YouTube Music", "Deezer", "Google Play Music",
  ];

  var webNamesItem = [];
  var webLinkLogoItem = [];

  bool isFilter = false;
  int currentIndex = 0;
  String currentName = '';

  TextEditingController serachText = TextEditingController();

  final AudioPlayer _player = AudioPlayer();

  @override
  void initState() {
    // TODO: implement initState
    super.initState();
    //webLinkLogoItem = webLinkLogos;
    webNamesItem = webNames;
    _player.currentIndexStream.listen((index) {
      if(index != null){
        updateIndex(index);
      }
    });
  }

  void filterSearchResults(String query) {
    setState(() {
      webNamesItem = webNames
          .where((item) => item.toLowerCase().contains(query.toLowerCase()))
          .toList();
    });
  }

  void changeFilteringView(){
    setState(() {
      isFilter = !isFilter;
    });
  }

  void updateIndex(int index){
    setState(() {
      if(webNames.isNotEmpty){
        currentName = webNames[index];
        currentIndex = index;
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    if (isFilter){
      return Scaffold(
        backgroundColor: myColors.white,
        body: Center(
          child: Container(
            decoration: BoxDecoration(
              shape: BoxShape.rectangle,
              color: Colors.grey.shade50,
              borderRadius: BorderRadius.all(Radius.circular(8)),
            ),
            child: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [

                Text(
                  webNames[currentIndex],
                  style: TextStyle(
                      color: Colors.black,
                      fontSize: 24
                  ),
                ),
                SizedBox(height: 20,),

                Text(
                  webLinks[currentIndex],
                  style: TextStyle(
                      color: Colors.blue,
                      fontSize: 24
                  ),
                ),
                SizedBox(height: 40,),

                Text(
                  "X Access Denied! X",
                  style: TextStyle(
                      color: Colors.red,
                      fontWeight: FontWeight.bold,
                      fontSize: 24
                  ),
                ),

              ],
            )
          ),
        ),
      );
    }
    return Scaffold(
      backgroundColor: myColors.white,

      body: Container(
        child: Column(
          children: <Widget>[
            Padding(
              padding: EdgeInsets.symmetric(horizontal: 18, vertical: 24),
              child: TextFormField(
                onChanged: (value) {
                  filterSearchResults(value);
                },
                validator: (value) {
                  if (value!.isEmpty) {
                    return 'Please enter a link';
                  } else if (!Uri.parse(value!).isAbsolute) {
                    return 'Please enter a valid URL';
                  }
                  return null;
                },
                controller: serachText,
                decoration: InputDecoration(
                    labelText: "Search",
                    hintText: "Search",
                    prefixIcon: Icon(Icons.search),
                    suffixIcon: IconButton(
                      onPressed: () async {
                        changeFilteringView();
                        // if (await canLaunchUrl(Uri.parse(serachText.text))) {
                        //   await launchUrl(Uri.parse(serachText.text));
                        // } else {
                        //   throw 'Could not launch $webLinks[index]';
                        // }
                      },
                      icon: Icon(Icons.arrow_forward, color: myColors.green,),
                    ),
                    border: OutlineInputBorder(
                        borderRadius: BorderRadius.all(Radius.circular(25.0)))),
              ),
            ),

            Expanded(
              child: ListView.builder(
                shrinkWrap: true,
                itemCount: webNamesItem.length,
                itemBuilder: (context, index) {
                  return ListTile(
                    onTap: () async {
                      changeFilteringView();
                      // if (await canLaunchUrl(Uri.parse(webLinks[index]))) {
                      //   await launchUrl(Uri.parse(webLinks[index]));
                      // } else {
                      //   throw 'Could not launch $webLinks[index]';
                      // }
                    },
                    title: Text('${webNamesItem[index]}'),
                    leading: CachedNetworkImage(
                      imageUrl: webLogos[index],
                      width: 32,
                      height: 32,
                      placeholder: (context, url) => CircularProgressIndicator(),
                      errorWidget: (context, url, error) => Icon(Icons.error),
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),

    );
  }
}